import { useState } from "react";

export default function ProfileSetup() {
  const [profileImage, setProfileImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProfileImage(URL.createObjectURL(file));
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-lg">
        <h2 className="text-center text-2xl font-bold text-gray-700 mb-6 bg-brown-300 py-2 rounded-lg">Set Your Profile</h2>
        <div className="flex flex-col space-y-4">
          <input type="text" placeholder="Name" className="w-full p-3 border rounded bg-gray-100" />
          <input type="email" placeholder="Email Address" className="w-full p-3 border rounded bg-gray-100" />
          <input type="tel" placeholder="Contact Number" className="w-full p-3 border rounded bg-gray-100" />
          <input type="date" placeholder="Wedding Date" className="w-full p-3 border rounded bg-gray-100" />
          <input type="text" placeholder="Budget Range" className="w-full p-3 border rounded bg-gray-100" />
          <div className="flex items-center justify-center flex-col">
            <div className="w-24 h-24 rounded-full bg-gray-200 flex items-center justify-center overflow-hidden">
              {profileImage ? <img src={profileImage} alt="Profile" className="w-full h-full object-cover" /> : <span className="text-gray-500">No Image</span>}
            </div>
            <label className="mt-2 bg-blue-500 text-white px-4 py-2 rounded cursor-pointer text-sm">
              Upload Your Picture
              <input type="file" accept="image/*" className="hidden" onChange={handleImageUpload} />
            </label>
          </div>
          <button className="w-full bg-brown-400 text-white p-3 rounded-lg font-bold hover:bg-brown-500">Save Profile</button>
        </div>
      </div>
    </div>
  );
}
