import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function CoupleDashboard() {
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState(null);
  const user_id = localStorage.getItem("user_id");

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/couple/dashboard/${user_id}`);
        const data = await response.json();

        if (data.status === "success") {
          console.log("Fetched Dashboard Data:", data.data);
          setDashboardData(data.data);
        } else {
          console.error("Error fetching dashboard:", data.message);
        }
      } catch (error) {
        console.error("API Error:", error);
      }
    };

    if (user_id) fetchDashboardData();
  }, [user_id]);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/");
  };

  const handleEditProfile = () => {
    navigate("/couple-profile");
  };

  return (
    <div className="fixed inset-0 w-full h-full bg-blue-100 overflow-hidden">
      <div className="h-full overflow-y-auto">
        {/* Header */}
        <header className="bg-orange-300 p-4 flex justify-between items-center fixed w-full top-0 left-0 z-10 shadow-lg">
          <img src="WEDNEST_LOGO.png" alt="WedNest Logo" className="h-20 w-auto" />
          <div className="flex items-center gap-6">
            <button className="text-lg">Home</button>
            <span className="text-2xl">🛒</span>
            <span className="text-2xl">👤</span>
          </div>
        </header>

        {/* User Profile (Top Right) */}
        <div className="absolute top-[80px] right-6 bg-white p-4 rounded-lg shadow-md w-60 text-center">
          <h2 className="text-lg font-semibold text-black">Welcome</h2>
          <div className="w-20 h-20 bg-gray-400 rounded-full mx-auto">
            {dashboardData?.profile_image ? (
              <img src={dashboardData.profile_image} alt="Profile" className="w-full h-full rounded-full object-cover" />
            ) : (
              <div className="w-full h-full flex items-center justify-center bg-gray-300 rounded-full">No Image</div>
            )}
          </div>
          <p className="font-semibold text-black">@{dashboardData?.username || "User"}</p>
          <p className="text-gray-600">{dashboardData?.email || "user@example.com"}</p>
          <button onClick={handleEditProfile} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded">
            Edit Profile
          </button>
        </div>

        {/* Main Content */}
        <div className="pl-[5%] pt-[140px] pr-6">
          <div className="grid grid-cols-3 gap-6 p-6">
            {/* Budget and Welcome Back Section */}
            <div className="col-span-2 grid grid-cols-2 gap-6">
              <div
                className="p-6 rounded-lg text-black bg-cover bg-center flex flex-col items-center justify-center shadow-md"
                style={{ backgroundImage: "url('/bgcouple.jpg')", height: "300px", width: "100%" }}
              >
                <h2 className="text-xl font-semibold">Budget</h2>
                <p className="text-lg">Budget Set: ${dashboardData?.budget?.total ?? 0}</p>
                <p className="text-lg">Remaining: ${dashboardData?.budget?.remaining ?? 0}</p>
              </div>

              <div
                className="p-6 rounded-lg text-center text-black bg-cover bg-center flex flex-col items-center justify-center shadow-md"
                style={{ backgroundImage: "url('/bgcouple.jpg')", height: "300px", width: "100%" }}
              >
                <h2 className="text-xl font-semibold">
                  Welcome Back, {dashboardData?.username || "User"}!
                </h2>
                <p className="text-lg">
                  Your big day on:{" "}
                  {dashboardData?.wedding_date
                    ? new Date(dashboardData.wedding_date).toLocaleDateString("en-US", {
                        year: "numeric",
                        month: "long",
                        day: "numeric",
                      })
                    : "Not Set"}
                </p>
              </div>
            </div>

            {/* Vendors Booked Section */}
            <div
              className="p-6 rounded-lg text-black bg-cover bg-center flex flex-col items-center justify-center shadow-md"
              style={{ backgroundImage: "url('/bgcouple.jpg')", height: "300px", width: "100%" }}
            >
              <h2 className="text-xl text-center font-semibold">Vendors Booked</h2>
              {dashboardData?.booked_vendors?.length > 0 ? (
                <ul className="text-lg text-center">
                  {dashboardData.booked_vendors.map((vendor, index) => (
                    <li key={index} className="py-1">
                      {vendor.service_type} - {vendor.vendor_id}
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-lg text-center">No vendors booked</p>
              )}
            </div>
          </div>

          {/* Service Icons Section */}
          <div className="grid grid-cols-4 gap-6 text-center py-6">
            <div className="flex flex-col items-center">
              <span className="text-5xl">📸</span>
              <p className="text-lg">Photography</p>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-5xl">🎤</span>
              <p className="text-lg">Entertainment</p>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-5xl">🎂</span>
              <p className="text-lg">Catering</p>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-5xl">💐</span>
              <p className="text-lg">Decorations</p>
            </div>
          </div>

          {/* Logout Button */}
          <div className="text-center mt-6">
            <button onClick={handleLogout} className="bg-red-500 text-white px-6 py-2 rounded shadow-md">
              Log Out
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
