import { useState } from "react";

export default function ProfileSetup() {
  const [profileImage, setProfileImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProfileImage(URL.createObjectURL(file));
    }
  };

  return (
    <div>
      <header className="bg-orange-300 p-4 flex justify-between items-center fixed w-full top-0 left-0 z-10 shadow-lg">
        <img src="WEDNEST_LOGO.png" alt="WedNest Logo" className="h-24 w-auto" />
        <div className="flex gap-6">
          <button className="text-lg">Home</button>
          <span className="text-lg">ðŸ›’</span>
          <span className="text-lg">ðŸ‘¤</span>
        </div>
      </header>
      <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-[#D2B48C] to-[#E6CBA8] p-6 pt-24">
        <div className="bg-white p-8 rounded-2xl shadow-2xl w-full max-w-2xl border border-gray-200">
          <h2 className="text-center text-4xl font-bold text-white mb-6 bg-gradient-to-r from-[#A67B5B] to-[#8B5E3C] py-4 rounded-lg shadow-md">Set Your Profile</h2>
          
          <div className="flex gap-8">
            <div className="w-1/2 flex flex-col space-y-4">
              <input type="text" placeholder="Name" className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm focus:ring-2 focus:ring-[#A67B5B]" />
              <input type="email" placeholder="Email Address" className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm focus:ring-2 focus:ring-[#A67B5B]" />
              <input type="tel" placeholder="Contact Number" className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm focus:ring-2 focus:ring-[#A67B5B]" />
              <label className="text-[#8B5E3C] font-semibold">Wedding Date</label>
              <input type="date" className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm focus:ring-2 focus:ring-[#A67B5B]" />
              <input type="text" placeholder="Budget Range" className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm focus:ring-2 focus:ring-[#A67B5B]" />
            </div>
            
            <div className="w-1/2 flex flex-col items-center">
              <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center overflow-hidden border-4 border-[#A67B5B] shadow-md">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <span className="text-gray-500">No Image</span>
                )}
              </div>
              <label className="mt-4 bg-[#8B5E3C] text-white px-4 py-2 rounded-lg cursor-pointer text-sm shadow-lg hover:bg-[#A67B5B] transition-all">
                Upload Your Picture
                <input type="file" accept="image/*" className="hidden" onChange={handleImageUpload} />
              </label>
            </div>
          </div>

          <div className="mt-6 flex justify-center">
            <button className="w-1/2 bg-gradient-to-r from-[#A67B5B] to-[#8B5E3C] text-white py-3 rounded-lg font-bold shadow-lg hover:opacity-90 transition-all">Save Profile</button>
          </div>
        </div>
      </div>
    </div>
  );
}
