import { useState } from "react";

export default function ProfileSetup() {
  const [profileImage, setProfileImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProfileImage(URL.createObjectURL(file));
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50 p-6">
      <div className="bg-white p-8 rounded-2xl shadow-lg w-full max-w-2xl">
        <h2 className="text-center text-3xl font-semibold text-gray-800 mb-6 bg-brown-300 py-3 rounded-lg">Set Your Profile</h2>
        
        <div className="flex gap-8">
          <div className="w-1/2 flex flex-col space-y-4">
            <input type="text" placeholder="Name" className="w-full p-3 border rounded-lg bg-gray-100" />
            <input type="email" placeholder="Email Address" className="w-full p-3 border rounded-lg bg-gray-100" />
            <input type="tel" placeholder="Contact Number" className="w-full p-3 border rounded-lg bg-gray-100" />
            <input type="date" placeholder="Wedding Date" className="w-full p-3 border rounded-lg bg-gray-100" />
            <input type="text" placeholder="Budget Range" className="w-full p-3 border rounded-lg bg-gray-100" />
          </div>
          
          <div className="w-1/2 flex flex-col items-center">
            <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center overflow-hidden border-2 border-gray-300">
              {profileImage ? (
                <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
              ) : (
                <span className="text-gray-500">No Image</span>
              )}
            </div>
            <label className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-lg cursor-pointer text-sm shadow-md hover:bg-blue-600">
              Upload Your Picture
              <input type="file" accept="image/*" className="hidden" onChange={handleImageUpload} />
            </label>
          </div>
        </div>

        <div className="mt-6 flex justify-center">
          <button className="w-1/2 bg-brown-500 text-white py-3 rounded-lg font-bold shadow-md hover:bg-brown-600">Save Profile</button>
        </div>
      </div>
    </div>
  );
}
