const userId = localStorage.getItem("user_id");

const fetchProfile = async () => {
    try {
        const response = await fetch(`http://localhost:3000/api/couple/profile/${userId}`);
        const data = await response.json();

        if (data.status === "success") {
            setFormData({
                name: data.data.username || "",
                email: data.data.email || "",
                contactNumber: data.data.contact_number || "",
                weddingDate: data.data.wedding_date || "",
                budgetRange: data.data.budget || "",
            });

            if (data.data.profile_image) {
                setPreviewImage(data.data.profile_image); 
            }
        } else {
            setMessage("Failed to load profile.");
        }
    } catch (error) {
        console.error("Error fetching profile:", error);
        setMessage("Server error, unable to fetch profile.");
    }
};

// ✅ Fix Image Upload
const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
        setProfileImage(file);
        setPreviewImage(URL.createObjectURL(file)); 
    }
};

