import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const VendorProfileSetup = () => {
  const navigate = useNavigate(); // Initialize navigate
  const [vendorData, setVendorData] = useState({
    businessName: "",
    vendorType: "",
    contactNumber: "",
    email: "",
    location: "",
    pricing: "",
    profileImage: null,
  });
  const [previewImage, setPreviewImage] = useState("");
  const [loading, setLoading] = useState(false);

  const user_id = "replace_with_vendor_id"; // Replace with actual vendor ID from auth

  // Fetch vendor profile on mount
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(
          `http://localhost:3000/api/vendor/profile/${user_id}`
        );
        const result = await response.json();

        if (result.status === "success") {
          setVendorData({
            businessName: result.data.businessName || "",
            vendorType: result.data.vendorType || "",
            contactNumber: result.data.contactNumber || "",
            email: result.data.email || "",
            location: result.data.location || "",
            pricing: result.data.pricing || "",
            profileImage: null,
          });
          if (result.data.profile_image) {
            setPreviewImage(result.data.profile_image);
          }
        }
      } catch (error) {
        console.error("Error fetching vendor profile:", error);
      }
    };

    fetchProfile();
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    setVendorData({ ...vendorData, [e.target.name]: e.target.value });
  };

  // Handle profile image upload
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setVendorData({ ...vendorData, profileImage: file });
      setPreviewImage(URL.createObjectURL(file)); // Show preview
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("user_id", user_id);
    formData.append("businessName", vendorData.businessName);
    formData.append("vendorType", vendorData.vendorType);
    formData.append("contactNumber", vendorData.contactNumber);
    formData.append("email", vendorData.email);
    formData.append("location", vendorData.location);
    formData.append("pricing", vendorData.pricing);
    if (vendorData.profileImage) {
      formData.append("profileImage", vendorData.profileImage);
    }

    try {
      const response = await fetch("http://localhost:3000/api/vendor/profile", {
        method: "PUT",
        body: formData,
      });

      const result = await response.json();

      if (result.status === "success") {
        alert("Profile updated successfully!");
        navigate("/vendor/dashboard"); // Redirect after successful update
      } else {
        alert("Error updating profile: " + result.message);
      }
    } catch (error) {
      console.error("Update error:", error);
      alert("Failed to update profile.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white">
      <div className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-3xl">
        <h2 className="text-3xl font-bold text-center text-black bg-[#B87C66] py-3 rounded-lg">
          Set Your Profile
        </h2>

        <form onSubmit={handleSubmit} className="mt-6 space-y-4">
          <div className="text-center">
            <label className="block text-lg text-black">Profile Picture</label>
            <div className="relative w-32 h-32 mx-auto">
              {previewImage ? (
                <img
                  src={previewImage}
                  alt="Profile Preview"
                  className="w-32 h-32 rounded-full object-cover border border-gray-300"
                />
              ) : (
                <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center text-gray-500">
                  No Image
                </div>
              )}
            </div>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              className="hidden"
              id="profile-upload"
            />
            <label
              htmlFor="profile-upload"
              className="mt-3 inline-block bg-[#B87C66] text-white py-2 px-4 rounded-lg text-lg hover:bg-[#9c6753] transition cursor-pointer"
            >
              Upload Picture
            </label>
          </div>

          <div>
            <label className="block text-lg text-black">Name of your Business</label>
            <input
              type="text"
              name="businessName"
              value={vendorData.businessName}
              onChange={handleChange}
              className="w-full px-4 py-2 border bg-gray-100 rounded-lg text-black"
              required
            />
          </div>

          <div>
            <label className="block text-lg text-black">Type</label>
            <input
              type="text"
              name="vendorType"
              value={vendorData.vendorType}
              onChange={handleChange}
              className="w-full px-4 py-2 border bg-gray-100 rounded-lg text-black"
              required
            />
          </div>

          <div>
            <label className="block text-lg text-black">Contact Number</label>
            <input
              type="tel"
              name="contactNumber"
              value={vendorData.contactNumber}
              onChange={handleChange}
              className="w-full px-4 py-2 border bg-gray-100 rounded-lg text-black"
              required
            />
          </div>

          <div>
            <label className="block text-lg text-black">Email Address</label>
            <input
              type="email"
              name="email"
              value={vendorData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 border bg-gray-100 rounded-lg text-black"
              required
            />
          </div>

          <div>
            <label className="block text-lg text-black">Location</label>
            <input
              type="text"
              name="location"
              value={vendorData.location}
              onChange={handleChange}
              className="w-full px-4 py-2 border bg-gray-100 rounded-lg text-black"
              required
            />
          </div>

          <div>
            <label className="block text-lg text-black">Pricing</label>
            <input
              type="text"
              name="pricing"
              value={vendorData.pricing}
              onChange={handleChange}
              className="w-full px-4 py-2 border bg-gray-100 rounded-lg text-black"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full mt-4 bg-[#B87C66] text-white py-3 rounded-lg text-lg hover:bg-[#9c6753] transition"
            disabled={loading}
          >
            {loading ? "Saving..." : "Save Profile"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default VendorProfileSetup;
